import React, {  useState,useEffect } from 'react';
import { useNavigate,Link } from 'react-router-dom';
import { useCookies } from 'react-cookie';
import CryptoJS, { SHA256 } from 'crypto-js';
//import Cookies from 'react-cookie';
import "./LoginForm.css";

const LoginForm = () => {
  const [name,setname]=useState("");
  const [password,setpassword]=useState("");
  const [dat,setDat]=useState(null);
  const navigate = useNavigate();
  const [cookies,setCookie]=useCookies(['session_id']);

  const ChangeHas=()=>{
    const CHname=name;
    const CHpass=CryptoJS.SHA256(password).toString(CryptoJS.enc.Hex).toUpperCase();
    let cht;
    console.log("CHpass",CHpass);
    console.log("SHA256:",SHA256(password));

    cht = handleSession(CHname,CHpass);
    //if(CHpass) console.log(CHpass);
  }

  const handleSession=(enmae,epass)=>{
    fetch(`https://localhost:7038/api/ChatSessionCtl?userId=${enmae}&password=${epass}`,{
      method:'POST',
      credentials:'include',
      headers:{
        "Content-Type": "application/json",
      }
    })
    .then((response) => response.json())
    .then((data) => {
      ChangeHas();
      setname("");      //入力した文字を消す
      setpassword("");  //入力した文字を消す
      setDat(data);     //dataがあるか確認するテスト
      setCookie('session_id',data.result.sessionId,{path:'/',sameSite:'none'});
      //session_idの名前、session_idの値、オプションでどこでも使えるようにした
      //console.log('cookie:',cookies);

      //ページ遷移を行う
      navigate("/Room");
    })
    .catch((error) => {
      console.error("Error during login:", error);
    });
  }

  return (
    <div className='logininput'>
      <input 
      type='text'
      value={name}
      onChange={(e)=>setname(e.target.value)}
      />
      <input
      type='text'
      value={password}
      onChange={(e)=>setpassword(e.target.value)}
      />
      <button onClick={ChangeHas}>Send</button>

      <div>
        新規作成はこちら<Link to={'/session'}>こちら</Link>
      </div>

      <div>
        {dat ? (
          <p>成功</p>
        ):(
          <p>データ取得中...</p>
        )}
      </div>
    </div>
  )
}
export default LoginForm
