import React,{useState} from "react";
import Gruup from "./Group.module.css";
//TODO:グループ一覧
const GroupButton = () => {
    const [Group,setGroup]=useState("");
    const ClickHandle=(e)=>{
      setGroup(e.target.value);
    }
    return (
      <div>
        <div className={Gruup.GrupDis}>
          <p></p>
        </div>
        <div className={Gruup.memberDis}>
          <input type='button' value='Name0' onClick={ClickHandle}/>
          <input type='button' value='Name1' onClick={ClickHandle}/>
          <input type='button' value='Name2' onClick={ClickHandle}/>
        </div>
        <label className={Gruup.NameDisplay}>
          <p onChange={setGroup}>Name:{Group}</p>
        </label>
      </div>
    );
}

export default GroupButton
