import React from 'react'

const textSend=()=>{
    const [text,settext]=useState("");
    return (
      <div>
        <input
         value={text}
         onChange={(event)=>settext(event.target.value)}
        />
        <p>{text}</p>
      </div>
    )
  };

export default textSend
