import React,{useState} from "react";
import "./App.css";

function App() {
  const values=[
    {id:1,item:"HTML"},
    {id:2,item:"JavaScript"},
    {id:3,item:"CSS"}
  ];
  const SelectItem=values.map((value)=>{
    return (
      <option value={value.item} key={value.id}>
        {value.item}
      </option>
    );
  });
  const InputSelectBox=()=>{
    const [selectValue,setSelectValue]=useState(values[0]['item']);
    const handleChange=(e)=>{
      setSelectValue(e.target.value);
    }
    return (
      <div className="App">
        <p>現在選択されている値：<b>{selectValue}</b></p>
        <select value={selectValue} onChange={handleChange}></select>
      </div>
    ); 
  };
  return (
    <div>
      <InputSelectBox/>
    </div>
  );
}

export default App;
