import React,{useState} from "react";

const Inputtext=()=>{
  const [InputTextValue,setInputTextValue]=useState("");
  const [text,setText]=useState("JavaScript");

  const handleClick=(e)=>{
    setText(InputTextValue);
    setInputTextValue("");
  };

  return (
    <div className="App">
      <h1>I Love {text}</h1>
      <input type="text" value={InputTextValue} onChange={handleClick}/>
      <input type="button" value="入力" onClick={handleClick}/>
    </div>
  );
};
function App() {
  return (
    <div>
      <Inputtext/>
    </div>
  );
}

export default App;
