import React, {  useState } from 'react';
import { useNavigate,Link } from 'react-router-dom';
import { useCookies } from 'react-cookie';
import CryptoJS from 'crypto-js';
import "./LoginForm.css";

const LoginForm = () => {
  const [name,setname]=useState("");
  const [password,setpassword]=useState("");
  const [dat,setDat]=useState(null);
  const navigate = useNavigate();
  const [cookies,setCookie]=useCookies();

  const ChangeHas=()=>{
    const CHname=name;
    //ハッシュに変換したのはいいがfetchに載せたらハッシュ値で送られるからパスワードが間違えていると言われるはず
    const CHpass=CryptoJS.SHA256(password).toString(CryptoJS.enc.Hex).toUpperCase();


  }

  const handleSession=()=>{    
    fetch(`https://localhost:7038/api/ChatSessionCtl?userId=${name}&password=${password}`,{
      method:'POST',
      credentials:'include',
      headers:{
        "Content-Type": "application/json"
      },
    })
    .then((response)=>{return response.json()})
    .then((data) => {
      console.log("data",data);
      setname("");
      setpassword("");
      setDat(data);
      setCookie(`${data.result.userId}`,data,'/chat');
      navigate("/chat");
    })
    .catch((error) => {
      console.error("Error during login:", error);
    });
  }
  return (
    <div className='logininput'>
      <input 
      type='text'
      value={name}
      onChange={(e)=>setname(e.target.value)}
      />
      <input
      type='text'
      value={password}
      onChange={(e)=>setpassword(e.target.value)}
      />
      <button onClick={handleSession}>Send</button>

      <div>
        新規作成はこちら<Link to={'/session'}>こちら</Link>
      </div>

      {dat ? (
        <div>
          <p>成功</p>
        </div>
        
      ):(
        <p>データ取得中...</p>
      )}
    </div>
  )
}

export default LoginForm
