import React, { useState } from "react";
import "./Gruup.css";

const GroupButton = () => {
  const [group, setGroup] = useState("");
  const [buttonNames, setButtonNames] = useState(["Name0", "Name1", "Name2"]);

  const clickHandle = (e) => {
    setGroup(e.target.value);
  };

  return (
    <div className="Gruup">
      <div className="GrupDis">
        <p>グループ一覧</p>
      </div>

      <div className="NameDisplay">
        <p onChange={setGroup}>{group}</p>
      </div>

      <div className="memberDis">
        {buttonNames.map((name, index) => (
          <input
            key={index}
            className={`GrupBtn${index + 1}`}
            type="button"
            value={name}
            onClick={clickHandle}
          />
        ))}
      </div>
    </div>
  );
};

export default GroupButton;
