import React,{useState} from "react";
import "./App.css"

function App() {
  const Inputtext=()=>{
    //配列の中身は [状態変数、状態を変更するための関数] の２つがある
    //useStateの中身は状態の初期値を表している
    const [InputTextValue,setInputTextValue]=useState("");
    const [text,setText]=useState("JavaScript");

    const handleChange=(e)=> setInputTextValue(e.target.value);

    //setTextで
    const handleClick=()=>{
      setText(InputTextValue);
      setInputTextValue("");
    };

    return (
      <div className="App">
        <h1>I Love {text}</h1>
        <input type="text" value={InputTextValue} onChange={handleChange}/>
        <input type="button" value="入力" onClick={handleClick}/>
      </div>
    );
  };
  return (
    <div>
      <Inputtext/>
    </div>
  );
}

export default App;
