import React,{useState} from 'react';

const TextSend = () => {
    const [text, settext] = useState("");
    const ClickHandler=()=>{
      settext(text);
    }
    return (
      <div>
        <input
         value={text}
         onChange={(e) => settext(e.target.value)}
        />
        <input
        type='button'
        value="Send"
        onClick={ClickHandler}
        />
        <p>{text}</p>
      </div>
    );
  };

export default TextSend
