import React, { useState } from "react";
import "./Message.css";

const MessageApp = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [sender, setSender] = useState("Me");

  const handleMessageSend = () => {
    const newMessage = {
      message:inputValue,
      result:{
        "sessionId": "73a193c6-cf41-414c-8174-73b485d247cd",
        "userId": "user1",
        "expirationDate": "2023-11-12T21:49:08.4003742"
      },
      status:true
    };
    fetch(`https://localhost:7038/api/ChatCtl?message=${inputValue}&room=0`, {
      method: 'POST',
      //credentials: "include",
      mode:'cors',
      headers: {
        "Accept": "application/json",
        "Cookie": "session_id=416811df-f778-4714-bdf9-2af8bb693c6;",
        'Access-Control-Request-Method': 'POST'
      },
      body: JSON.stringify(newMessage),
    })
    .then((response) => response.json())
    .then((data) => {
      setMessages([data]);
      setInputValue("");
      setSender("Me");
      console.log(data)
    })
    .then((text)=>{
      return(
        <div className="message-container">
      {messages.map((index) => (
          <div key={index} className="message">
            <strong>{sender}: </strong> <p>{index.text}</p>
          </div>
      ))}
      </div>
      )
    })
    .catch((error) => {console.error("Error sending message:", error)});
  };
  return (
    <div>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="input-text"
        />
        <button onClick={handleMessageSend} className="send-button">
          Send
        </button>        
      </div>
    </div>
  );
};

export default MessageApp;
