import React,{useState} from "react";

function App() {
  const LogoutButton = (props) =>(
    <button onClick={props.toggleIsLoggedIn}>ログアウト</button>
  );
  const LoginButton=(props)=>(
    <button onClick={props.toggleIsLoggedIn}>ログイン</button>
  );

  const LoginControl =()=>{
    const [isLoggedIn,setIsLoggedInState]=useState(false);//isLoggedInはオブジェクト、
    const toggleIsLoggedIn=()=>{
      setIsLoggedInState(!isLoggedIn);
    };
    if(isLoggedIn){
      return <LogoutButton toggleIsLoggedIn={toggleIsLoggedIn} />;
    }
    return <LoginButton toggleIsLoggedIn={toggleIsLoggedIn}/>;
  }

  const Hello=()=>{
    const isReact=false;
    return <p>{isReact ? "Hello React":"Hello"}</p>
  }

  const handleClick=(e)=>{
    alert();
  }
  const SampleButton=()=>{
    return <input type="button" value="ボタン" onClick={handleClick}/>;
  }

  return (
    <div>
      <LoginControl/>
      <Hello/>
      <SampleButton/>
    </div>
  );
}

export default App;
