import React, { useState } from "react";
import "./Message.css";
import axios from "axios";

const MessageApp = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [sender, setSender] = useState("Me");

  const handleMessageSend = () => {
    let newMessage = [{
      'message':inputValue,
      'status':true,
      'result':{
        "message": `${inputValue}`,
        "user": "user1",
        "date": "2023-11-05T12:49:47.892Z",
        "hidden": false,
        "message_id": 42,
        "room": 0,
        "edited": false,
        "nickname_jp": "user1",
        "nickname_en": "user1"
      }
    }];

    axios.post(`https://localhost:7038//api/ChatCtl?message=${inputValue}&room=0`, newMessage, {
      headers: {
        "Accept": "application/json",
        "Cookie": "session_id=<416811df-f778-4714-bdf9-2af8bb693c6>",
        'Access-Control-Request-Method': 'POST',
      },
    })

    /*fetch(`https://localhost:7038/api/ChatCtl?message=${inputValue}&room=0`, {
      method: 'POST',
      //credentials:'include',
      //mode:'cors',
      headers: {
        "Accept": "application/json",
        "Cookie": "session_id=<416811df-f778-4714-bdf9-2af8bb693c6>",
        'Access-Control-Request-Method': 'POST',
      },
      body: JSON.stringify(newMessage),
    })*/
    .then((response) => {return response.json()})
    .then((data) => {
      setMessages([...messages,data]);
      setInputValue("");
      setSender("Me");
      console.table(data)
    })
    .catch((error) => {console.error("Error sending message:", error)});
  };
  return (
    <div>
      <div className="message-container">
        {messages.map((message,index) => (
            <div key={index} className="message">
              <strong>{sender}: </strong> <p>{message.index}</p>
            </div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="input-text"
        />
        <button onClick={handleMessageSend} className="send-button">
          Send
        </button>        
      </div>
    </div>
  );
};

export default MessageApp;
