import React,{useState} from "react";
import { Link,Route,useParams } from "react-router-dom";
import "./Gruup.css";

const GroupButton = () => {
    const [Group,setGroup]=useState("");
    const ClickHandle=(e)=>{
      setGroup(e.target.value);
    }
    //TODO:２つのグループ化にする
    return (
      <div className="Gruup">
        <div className="GrupDis">
            <p>グループ一覧</p>
        </div>

        <div className="NameDisplay">
            <p>{Group}</p>
        </div>


        <div className="memberDis">
          <Link to="/message-screen/Name0">
            <input className="GrupBtn1" type='button' value='Name0' onClick={ClickHandle}/>
          </Link>
          <Link to="/message-screen/Name1">
            <input className="GrupBtn2" type='button' value='Name1' onClick={ClickHandle}/>
          </Link>
          <Link to="/message-screen/Name2">
            <input className="GrupBtn3" type='button' value='Name2' onClick={ClickHandle}/>
          </Link>
        </div>

          <Route path="/messagescreen/:name" Component={MessageScreen}/>
      </div>
    );
}
const MessageScreen = () => {
  let { name } = useParams();

  return (
    <div>
      <h2>メッセージ画面 {name}</h2>
    </div>
  );
};

export default GroupButton
