import React,{useState} from "react";
//TODO:グループ一覧
const GroupMenu=[
    {id:1,Gurup:"Name0"},
    
]
const GroupButton = () => {
    const [Group,setGroup]=useState(["Name0","Name1","Name2"]);
    const ClickHandle=()=>{
      setGroup((Group,index)=>(index===2?"Name1":Group))
    }
    return (
      <div>
        <button onClick={ClickHandle}>Change</button>
        <label>
          <p onChange={ClickHandle}>Name:{Group}</p>
        </label>
      </div>
    );
}

export default GroupButton
