import React, { useState, useRef, useEffect } from 'react';
import MessageApp from './MessageApp';
import { useCookies } from 'react-cookie';
import axios from 'axios'; // axiosを追加

const Room = () => {
  const messagesEndRef = useRef(null);
  const [room, setRoom] = useState([]);
  const [selectRoom, setSelectRoom] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(['session_id']);

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth", block: "end" });
    }
  };

  useEffect(() => {
    axios.get('https://localhost:7038/api/ChatSessionCtl', {
      withCredentials: true, // credentials:'include' と同等
      headers: {
        'Content-Type': 'application/json',
      },
    })
    .then((response) => {
      const cookieHead = response.headers['set-cookie'];
      console.log(cookieHead);
      return response.data;
    })
    .then((data) => {
      console.log('sxk,cookie:', cookies);
      console.log('sck', data);
      //setCookie('session_id',data.result.sessionId);
    })
  }, []); // 空の配列を渡して初回のみ実行されるように修正

  const getRoom = () => {
    axios.get('https://localhost:7038/api/ChatRoomCtl', {
      withCredentials: true, // credentials:'include' と同等
      headers: {
        'Content-Type': 'application/json',
      },
    })
    .then((response) => {
      console.log("successRoomData:", response.data);
      const roomIds = response.data.result.map(e => e.name);
      setRoom([...roomIds]);
      console.log(room);
    })
    .catch((error) => {
      console.log("error:", error);
    });
  }

  const handleRoomClick = (roomId) => {
    setSelectRoom(roomId);
  }

  return (
    <div>
      <h1>Room</h1>
      <button onClick={getRoom}>更新</button>
      <div>
        {room.map((roomId, index) => (
          <button key={index} onClick={() => handleRoomClick(roomId)}>{roomId}</button>
        ))}
      </div>
      {selectRoom && <MessageApp room={selectRoom}/>}
      <div ref={messagesEndRef}></div>
    </div>
  )
}

export default Room;
