import React, { useState } from "react";
import "./Message.css";

const MessageApp = ({ activeMember }) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [sender, setSender] = useState("Me");

  const handleMessageSend = () => {
    if (inputValue.trim() !== "") {
      const newMessage = {
        text: inputValue,
        sender: sender,
      };
      //const queryParams = new URLSearchParams({ message: inputValue, room: 0 });

      fetch(`https://localhost:7038/api/ChatCtl`, {
        method: 'POST',
        mode:'cors',
        headers: {
          "Content-Type": "application/json",
          Origin: 'https://localhost:7038',
        },
        body: JSON.stringify(newMessage),
      })
        .then((response) => response.json())
        .then((json)=>setMessages(json))
        .then((data) => {
          setMessages([...messages, data]);
          setInputValue("");
          if (sender === "Me") {
            setSender("You");
          } else {
            setSender("Me");
          }
        })
        .catch((error) => console.error("Error sending message:", error));
        //session_idを持ってないから送ってもエラーが出るのか？
        //そもそもswaggerに送ることはできるのか？ASP.netの方に送るのでは？
        ajax({
          type:"post",
          url:"/api/ChatCtl",
          data:JSON.stringify(newMessage),
          contentType:'application/json',
          dataType:"json",
          success:function(json_data){
            if (!json_data[0]) {    // サーバが失敗を返した場合
              alert("Transaction error. " + json_data[1]);
              return;
            }
            location.reload();
          },
          error: function() {         // HTTPエラー時
            alert("Server Error. Please try again later.");
          },
          complete: function() {      // 成功・失敗に関わらず通信が終了した際の処理
              button.attr("disabled", false);  // ボタンを再び enableにする
          }
        });
  }
};

  return (
    <div>
      <div className="message-container">
        {messages.map((message, index) => (
          <div key={index} className="message">
            <strong>{message.sender}: </strong> {message.text}
          </div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="input-text"
        />
        <button onClick={handleMessageSend} className="send-button">
          Send
        </button>
        
      </div>
    </div>
  );
};

export default MessageApp;
