//Reactモジュール
import React, {useState,useRef, useEffect}from 'react';
//パッケージ
import { useCookies } from 'react-cookie';
import { json, useNavigate } from 'react-router-dom';
//外部フレームワーク
import { motion,AnimatePresence } from 'framer-motion';
//CSS
import './Room.css';

const Room = () => {
    const messagesEndRef = useRef(null);
    const [room,setroom]=useState([]);
    const [unionRoom,setunionRoom]=useState([]);
    const [cookies,setCookie,removeCookie]=useCookies(['session_id']);
    const navigate = useNavigate();
    let roomNameID=[];


    const scrollToBottom = () => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: "smooth", block: "end" });
        }
    };

    useEffect(()=>{
        setTimeout(()=>{
            getRoom();
        },4000);
    },[])

    const getRoom=()=>{
        fetch(`https://localhost:7038/api/ChatRoomCtl`,{
            credentials:'include',
            headers:{
                'Content-Type':'application/json',
            }
        })
        .then((response)=>{
            return response.json();
        })
        .then((data)=>{
            console.log("successRoomData:",data);
            const roomName=data.result.map(e=>e.name);
            roomNameID=data.result.map(e=>e.room_id);
            setroom([...roomName]);
            setunionRoom([...roomName]);
        })
        .catch((error)=>{console.log("error:",error)})
        .finally(()=>scrollToBottom());
    }

    const handleRoomClick=(roomid)=>{
        const Romnum=unionRoom.indexOf(roomid);
        if((Romnum!==-1)){
            console.log("成功")
        }else{
            console.log("ありませんでした。");
        }
        
        //roomidは名前だけ取っているので、これがどこの番号か知りたい
        console.log(roomid);
        navigate('/chat',{state:{name:Romnum}});
    }
    const changeAddRom=()=>{
        navigate('/AddGroup');
    }
    const Logout=()=>{
        removeCookie('sessionId');
        navigate('/');
    }
    const DeleteRoom=()=>{

        fetch("https://localhost:7038/api/ChatRoomCtl/Delete",{
            method:'DELETE',
            credentials:'include',
            headers:{
                'Content-Type':'application/json'
            }
        })
        .then((response)=>{
            console.log("削除",response);
            return json(response);
        })


    }
    
    return (
        <div className='room'>
            <div className='Header'>
                <h1>ChatApp</h1>
                <button onClick={Logout} className='logout'>ログアウト</button>
                <h3>Room</h3>
            </div>
            <button onClick={changeAddRom} className='addR'>追加</button>

            <div className='container'>
                {room.map((data, index) => (
                <button key={index} onClick={() => handleRoomClick(data)} className='roomdata'>{data}</button>
                ))}            
            </div>
        </div>
    )
}

export default Room
