import React,{useState} from "react";
/*import Gruup from "./Group.module.css";*/
const GroupButton = () => {
    const [Group,setGroup]=useState("");
    const ClickHandle=(e)=>{
      setGroup(e.target.value);
    }
    //TODO:２つのグループ化にする
    return (
      <div className="Gruup">
        <div className={Gruup.GrupDis}>
            <p>グループ一覧</p>
        </div>

        <div className={Gruup.NameDisplay}>
            <p onChange={setGroup}>Name:{Group}</p>
        </div>


        <div className={Gruup.memberDis}>
          <input id="GrupBtn1" type='button' value='Name0' onClick={ClickHandle}/>
          <input id="GrupBtn2" type='button' value='Name1' onClick={ClickHandle}/>
          <input id="GrupBtn3" type='button' value='Name2' onClick={ClickHandle}/>
         </div>
      </div>
    );
}

export default GroupButton
