import React,{useState} from "react";
import "./Gruup.css";
import { Link } from "react-router-dom";

const GroupButton = () => {
    const [Group,setGroup]=useState("");
    const ClickHandle=(e)=>{
      setGroup(e.target.value);
    }
    //TODO:２つのグループ化にする
    return (
      <div className="Gruup">
        <div className="GrupDis">
            <p>グループ一覧</p>
        </div>

        <div className="NameDisplay">
            <p>{Group}</p>
        </div>


        <div className="memberDis">
            <Link to='/MessageApp/' ><input className="GrupBtn1" type='button' value='Name0' /></Link>
            <input className="GrupBtn2" type='button' value='Name1' onClick={ClickHandle}/>
            <input className="GrupBtn3" type='button' value='Name2' onClick={ClickHandle}/>
        </div>
      </div>
    );
}
export default GroupButton
