import React,{useState} from "react";
//TODO:グループ一覧
const GroupButton = () => {
    const [Group,setGroup]=useState("");
    const ClickHandle=(e)=>{
      setGroup(e.target.value);
    }
    return (
      <div>
        <input type="text"/>
        <button onClick={ClickHandle}> Change </button>
        <label>
          <p onChange={setGroup}>Name:{Group}</p>
        </label>
      </div>
    );
}

export default GroupButton
