import React, { useState } from "react";
import "./Message.css";
import axios from "axios";
import { json } from "react-router-dom";

const baseURL='https://localhost:7038/api/ChatCtl';

const MessageApp = ({ activeMember }) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [sender, setSender] = useState("Me");

  const handleMessageSend = () => {
    if (inputValue.trim() !== "") {
      const newMessage = {
        text: inputValue,
        sender: sender,
      };
      axios.post(baseURL,{
        body:JSON.stringify(newMessage),
      })
      .then((response)=>{
        response.json(),
        setMessages(response);
      })
      //const queryParams = new URLSearchParams({ message: inputValue, room: 0 });

      /*fetch(`https://localhost:7038/api/ChatCtl`, {
        method: 'POST',
        mode:'cors',
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newMessage),
      })
        .then((response) => response.json())
        .then((json)=>setMessages(json))
        .then((data) => {
          setMessages([...messages, data]);
          setInputValue("");
          if (sender === "Me") {
            setSender("You");
          } else {
            setSender("Me");
          }
        })
        .catch((error) => console.error("Error sending message:", error));*/
        //session_idを持ってないから送ってもエラーが出るのか？
        //そもそもswaggerに送ることはできるのか？ASP.netの方に送るのでは？
      }
  };

  return (
    <div>
      <div className="message-container">
        {messages.map((message, index) => (
          <div key={index} className="message">
            <strong>{message.sender}: </strong> {message.text}
          </div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="input-text"
        />
        <button onClick={handleMessageSend} className="send-button">
          Send
        </button>
        
      </div>
    </div>
  );
};

export default MessageApp;
